data.value:
using System;
using System.Linq;
using Generics = System.Collections.Generic;

namespace SyntaxEditorDemo {

	using System.Diagnostics;
	using Int32List = Generics.List<int>;

	/// <summary>
	/// Represents the main program class, which contains the entry point of the application.
	/// </summary>
	public class Program {

		/// <summary>
		/// The main entry point of the application.
		/// </summary>
		/// <param name="args">The arguments.</param>
		public static void Main(string[] args) {

			// Build list of all possible numbers
			var allNumbers = new Generics.List<int>();
			for (int i = 0; i < 25; i++)
				allNumbers.Add(i);

			// Pick 5 random winning numbers
			var random = new Random();
			var randomNumbers = new Int32List();
			while (randomNumbers.Count < 5) {
				int j = random.Next(25);
				if (!randomNumbers.Contains(j))
					randomNumbers.Add(j);
			}

			// Get lists of the winning and losing numbers
			var winningNumbers = randomNumbers.OrderBy(i => i).ToList();
			var losingNumbers = allNumbers.Where(i => !randomNumbers.Contains(i)).ToList();

			// Sanity check
			Debug.Assert(winningNumbers.Count + losingNumbers.Count == allNumbers.Count, "Counts do not match");
			for (int i = 0; i < winningNumbers.Count; i++) {
				Debug.Assert(!losingNumbers.Contains(winningNumbers[i]),
					string.Format("The winning number {0} at index {1} was also a losing number", winningNumbers[i], i));
			}

			// Output results
			ConsoleWriter.Write("Winning Numbers: ");
			ConsoleWriter.Write(winningNumbers);
			ConsoleWriter.NewLine();

			ConsoleWriter.Write("Losing Numbers: ");
			ConsoleWriter.Write(losingNumbers);
			ConsoleWriter.NewLine();

			Console.ReadKey();
		}

		/// <summary>
		/// Represents a wrapper class for Console output.
		/// </summary>
		private static class ConsoleWriter {

			/// <summary>
			/// Writes a new-line to the <see cref="Console"/>.
			/// </summary>
			public static void NewLine() {
				Console.WriteLine();
			}

			/// <summary>
			/// Writes the specified <paramref name="item"/> to the <see cref="Console"/>.
			/// </summary>
			/// <typeparam name="T">The type of the item.</typeparam>
			/// <param name="item">The item to write.</param>
			private static void Write<T>(T item) {
				Console.Write("{0}", item);
			}

			/// <summary>
			/// Writes the specified <paramref name="items"/> to the <see cref="Console"/>.
			/// </summary>
			/// <typeparam name="T">The type of the items.</typeparam>
			/// <param name="items">The items to write.</param>
			public static void Write<T>(Generics.IEnumerable<T> items) {
				if (items == null)
					return;

				bool first = true;
				foreach (T item in items) {
					if (first)
						first = false;
					else
						Write(", ");

					Write(item);
				}
			}

			/// <summary>
			/// Writes the specified string to the <see cref="Console"/>.
			/// </summary>
			/// <param name="str">The string to write.</param>
			public static void Write(string str) {
				Console.Write(str);
			}

			// This syntax language implementation is part of the .NET Languages Add-on 
			// for SyntaxEditor, which is optional and sold separately from SyntaxEditor.

		}

	}
}
